document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("images-container"),t=document.getElementById("lightbox");e.addEventListener("click",e=>{const s=e.target.closest(".image-wrapper");if(s){const e=s.querySelector("img");e&&(t.innerHTML='<div class="close-lightbox"></div>'+e.outerHTML,t.classList.add("show"))}}),t.addEventListener("click",e=>{e.target.hasAttribute("src")||t.classList.remove("show")}),setTimeout(()=>e.classList.remove("loading"),1500)});
